import React from "react";
import Link from "next/link";
import Image from "./image";


const MoreProjects = ({articles, current}) => {

    // const findPrev = articles.find( current);
    const moreProj = []
   articles.find(
        function checkNumber( currentProject ) {
          if( currentProject.id === current - 1) {
              moreProj.push(currentProject)
          }
        }
    );

   articles.find(
        function checkNumber2( currentProject ) {
            if( currentProject.id === current + 1) {
                moreProj.push(currentProject)
            }
        }
    );
    console.log(moreProj)
    return (

        <section className="moreProjects">

            <div className="container">

                {moreProj.map((article) =>{

                    const firstImgEl = article.contentBlock.find(item => {
                        if(item.image){
                            return item
                        }
                    })
                    return(
                        <Link key={article.id} href={`/projects/${article.slug}`}>
                            <a className="card">
                                <div className="top">
                                    {firstImgEl ? <Image image={firstImgEl.image} /> : null}
                                </div>
                            </a>
                        </Link>
                    )
                    }
                )}
            </div>
        </section>
    );
};

export default MoreProjects;
