import React from "react";
import Text from "../components/text";
import Articles from "../components/articles";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import FullScreenVideo from "../components/fullScreenVideo";
import {getStrapiMedia} from "../lib/media";

const Home = ({ articles, categories, homepage }) => {
    const videoUrl = getStrapiMedia(homepage.hero.video);
    const [scroll, setScroll] = React.useState(0);

    const handleScroll = () => {
        setScroll(window.scrollY);
    };

    React.useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);


    const classNames = classnames(
        'header',
        scroll < 10 ? "" : "sticky",
        {
            [`header--${mode}`]:Boolean(mode),

        },
        className
    );
  return (
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
      <section className="hero">
        <video className='videoTag' autoPlay loop muted>
          <source src={videoUrl} type='video/mp4' />
        </video>
        <div className="container">
          <div className="text">
            <h1>{homepage.hero.heading}</h1>
            <p className="title">{homepage.hero.title}</p>
          </div>
        </div>
      </section>
      <Text text={homepage.textBlock}/>
      <FullScreenVideo video={homepage.video}/>
      {/*<div className="uk-section">*/}
      {/*  <div className="uk-container uk-container-large">*/}
          {/*<Articles articles={articles} />*/}
      {/*  </div>*/}
      {/*</div>*/}
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
  ]);

  return {
    props: { articles, categories, homepage },
    revalidate: 1,
  };
}

export default Home;
