import React from "react";
import classnames from "classnames"
import Text from "../components/text";
import Articles from "../components/articles";
import Columns from "../components/columns";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import FullScreenVideo from "../components/fullScreenVideo";
import {getStrapiMedia} from "../lib/media";
import FullScreenImage from "../components/fullscreenImage";
import NumericText from "../components/numericText";

const Home = ({ projects, categories, homepage }) => {
    // const videoUrl = getStrapiMedia(homepage.video);

  return (
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
      <section className="hero">
        <video className='videoTag' autoPlay loop muted>
          {/*<source src={videoUrl} type='video/mp4' />*/}
        </video>
        <div className="container">
          <div className="text">
            <h1>{homepage.hero.heading}</h1>
            <p className="title">{homepage.hero.title}</p>
          </div>
        </div>
      </section>
      <Text text={homepage.textBlock}/>
      <FullScreenVideo video={homepage.video}/>
      <Columns columns={homepage.columnsBlock} />
      <FullScreenImage image={homepage.singleImage}/>
      <NumericText rows={homepage.numericText}/>
      {/*<p>{homepage.columnsBlock}</p>*/}
      {/*<div className="uk-section">*/}
      {/*  <div className="uk-container uk-container-large">*/}
      <Articles articles={projects} count={4} />

      {/*  </div>*/}
      {/*</div>*/}
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [projects, articles, categories, homepage] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
      fetchAPI("/projects"),
      fetchAPI("/articles"),
    fetchAPI("/homepage"),
  ]);

  return {
    props: { projects, articles,  categories, homepage },
    revalidate: 1,
  };
}

export default Home;
