import React from "react";
import Link from "next/link";
import Image from "./image";


const MoreProjects = ({articles, current}) => {

    // const findPrev = articles.find( current);
    const moreProjectArray = [];

       articles.find(
            function checkNumber( currentProject ) {
              if( currentProject.id === current - 1 || currentProject.id === current + 1) {
                  moreProjectArray.push(currentProject)
              }
            }
        );

    return (

        <section className="moreProjects">
            <div className="container">
                {moreProjectArray.map((article) =>{
                    const prevText = "prev"
                    const nextText = "next"
                    const firstImgEl = article.contentBlock.find(item => {
                        if(item.image){
                            return item
                        }
                    })
                    return(
                        <Link key={article.id} href={`/projects/${article.slug}`}>
                            <a className="card">
                                <h2>{}</h2>
                                <div className="top">
                                    {firstImgEl ? <Image image={firstImgEl.image} /> : null}
                                </div>
                            </a>
                        </Link>
                    )
                    }
                )}
            </div>
        </section>
    );
};

export default MoreProjects;
