import React from "react";
import MarkdownView from 'react-showdown';

const Text = ({text}) => {
    return (
        <section className="textBlock">
            <div className="container">
                <div className="text">
                    {text.heading ? 
                        <MarkdownView
                            markdown={text.heading}
                            options={{ tables: true, emoji: true }}
                        />
                        : ''}
                    {text.about ? <p>{text.about}</p>:''}
                </div>
            </div>
        </section>
    );
};

export default Text;
