import React from "react";
import Link from "next/link";
import Image from "./image";


const MoreProjects = ({articles, current}) => {

    // const findPrev = articles.find( current);
    articles.find( // проверяем все ли элементы в массиве соответствуют условию заданному в передаваемой функции
        function checkNumber( currentProject ) {
            console.log(currentProject.id)  ; // проверяем является ли текущее значение меньше нуля
        } // если true, то возвращаем это значение (если массив не содержит такого значение, то undefined)
    );

    return (

        <section className="moreProjects">

            <div className="container">

                {articles.map((article) =>{

                    const firstImgEl = article.contentBlock.find(item => {
                        if(item.image){
                            return item
                        }
                    })
                    return(
                        <Link key={article.id} href={`/projects/${article.slug}`}>
                            <a className="card">
                                <div className="top">
                                    {firstImgEl ? <Image image={firstImgEl.image} /> : null}
                                </div>
                            </a>
                        </Link>
                    )
                    }
                )}
            </div>
        </section>
    );
};

export default MoreProjects;
