import React from "react";
import MarkdownView from 'react-showdown';

const Text = ({text}) => {
    let converter = new showdown.Converter(),
        orig      = text.about,
        html      = converter.makeHtml(orig);
    return (
        <section className="textBlock">
            <div className="container">
                <div className="text">
                    {text.heading ? <h2>{text.heading}</h2> : ''}
                    {text.about ?
                        html
                    //     <MarkdownView
                    //     markdown={text.about}
                    //     options={{ tables: true, emoji: true, prefixHeaderId:true, literalMidWordUnderscores:true, }}
                    // />
                    :''}
                </div>
            </div>
        </section>
    );
};

export default Text;
