import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../components/image";
import Seo from "../../components/seo";
import { getStrapiMedia } from "../../lib/media";

const Article = ({ projects  }) => {
    const imageUrl = getStrapiMedia(projects.image);

    // const seo = {
    //     metaTitle: article.title,
    //     metaDescription: article.description,
    //     shareImage: article.image,
    //     article: true,
    // };
// console.log(params.slug)
    return (
        <Layout >
            <Seo seo={seo} />
            <div
                id="banner"
                className="uk-height-medium uk-flex uk-flex-center uk-flex-middle uk-background-cover uk-light uk-padding uk-margin"
                data-src={imageUrl}
                data-srcset={imageUrl}
                data-uk-img
            >
                <h1>{projects.title}</h1>
            </div>
            <div className="uk-section">
                <div className="uk-container uk-container-small">
                    <ReactMarkdown source={projects.content} escapeHtml={false} />
                    <hr className="uk-divider-small" />
                    <div className="uk-grid-small uk-flex-left" data-uk-grid="true">
                        <div>
                            {projects.author.picture && (
                                <Image
                                    image={projects.author.picture}
                                    style={{
                                        position: "static",
                                        borderRadius: "50%",
                                        height: 30,
                                    }}
                                />
                            )}
                        </div>
                        <div className="uk-width-expand">
                            <p className="uk-margin-remove-bottom">
                                By {projects.author.name}
                            </p>
                            <p className="uk-text-meta uk-margin-remove-top">
                                <Moment format="MMM Do YYYY">{projects.published_at}</Moment>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </Layout>
    );
};

export async function getStaticPaths() {
    const projects = await fetchAPI("/projects");

    return {
        paths: projects.map((project) => ({
            params: {
                slug: project.slug,
            },
        })),
        fallback: false,
    };
}

export async function getStaticProps({ params }) {
    const projects = await fetchAPI(
        `/projects?slug=${params.slug}&status=published`
    );
    const categories = await fetchAPI("/categories");

    return {
        props: { projects: projects[0], categories },
        revalidate: 1,
    };
}

export default Article;
