import React, {useState} from "react";
import classnames from "classnames"
import Layout from "../components/layout";
import Seo from "../components/seo";
import {fetchAPI} from "../lib/api";
import VacanciesCard from "../components/vacanciesCards";
import ModalVacancies from "../components/Modal/modalVacancies";
import {Form, Input, Modal, Button} from "antd";


const Vacancies = ({ vacancies, categories, homepage,  }) => {
    const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
    const onFinish = (values) => {
        console.log('Success:', values);
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (
        <Layout pageClass="vacancies">
            <Seo seo={homepage.seo} />
            <section className="head">
                <div className="container">
                    <div className="text">
                        <h1>{vacancies.main.heading}</h1>
                        <p>{vacancies.main.about}</p>
                    </div>
                </div>
            </section>
            <section className="vacanciesCards">
                <div className="container">
                    { vacancies.CardVacancies ?
                        vacancies.CardVacancies.map((card) => {
                            return(
                                <VacanciesCard  key={card.id} card={card} showModal={showModal}/>
                                // console.log(card)
                            )})
                        : null}
                </div>
            </section>
            {/*<ModalVacancies />*/}
            <Modal title="Apply for a vacancy"
                visible={isModalVisible}
                onOk={handleOk}
                // onCancel={handleCancel}
            >
                <Form
                    name="Apply for a vacancy"
                    initialValues={{ remember: true }}
                    onFinish={onFinish}
                    onFinishFailed={onFinishFailed}
                >
                    <Form.Item
                        name="username"
                        rules={[{ required: true, message: 'Please input your name!' }]}
                    >
                        <Input placeholder="Username*"/>
                    </Form.Item>
                    <Form.Item
                        name="email"
                        rules={[{type: 'email', required: true, message: 'Please input your name!' }]}
                    >
                        <Input placeholder="Email*" />
                    </Form.Item>
                    <Form.Item
                        name="phone"
                        rules={[{ required: true, message: 'Please input your phone number!' }]}
                    >
                        <Input placeholder="Phone Number*" />
                    </Form.Item>
                    <Form.Item
                        name="portfolio link"
                    >
                        <Input placeholder="Portfolio link" />
                    </Form.Item>
                </Form>
            </Modal>
        </Layout>
    );
}
export async function getStaticProps() {
    // Run API calls in parallel
    const [vacancies, homepage] = await Promise.all([
        fetchAPI("/vacancies"),
        fetchAPI("/homepage"),
    ]);

    return {
        props: { vacancies, homepage },
        revalidate: 1,
    };
}
export default Vacancies;
