import React from "react";
import MarkdownView from 'react-showdown';

const Text = ({text}) => {
    return (
        <section className="textBlock">
            <div className="container">
                <div className="text">
                    {text.heading ? <h2>{text.heading}</h2>

                        : ''}
                    {text.about ?
                        <MarkdownView
                        markdown={text.about}
                        options={{ tables: true, emoji: true, prefixHeaderId:true }}
                    />:''}
                </div>
            </div>
        </section>
    );
};

export default Text;
