import React from "react";
import Link from "next/link";
import Image from "./image";


const MoreProjects = ({articles, current}) => {

    // const findPrev = articles.find( current);
    const moreProjectArray = []
   articles.find(
        function checkNumber( currentProject ) {
          if( currentProject.id === current - 1) {
              moreProjectArray.push(currentProject)
          }
        }
    );

   articles.find(
        function checkNumber2( currentProject ) {
            if( currentProject.id === current + 1) {
                moreProjectArray.push(currentProject)
            }
        }
    );
    console.log(moreProjectArray)
    return (

        <section className="moreProjects">

            <div className="container">

                {moreProjectArray.map((article) =>{

                    const firstImgEl = article.contentBlock.find(item => {
                        if(item.image){
                            return item
                        }
                    })
                    return(
                        <Link key={article.id} href={`/projects/${article.slug}`}>
                            <div className="top">
                                {firstImgEl ? <Image image={firstImgEl.image} /> :''}
                            </div>
                            <div className="cardBody">
                                <p className="name">
                                    {article.projectName}
                                </p>
                                {firstTxtEl.about.length >=120 ? <p className="title">{firstTxtEl.about.substring(0,120) + '...'}</p> : <p className="title">{firstTxtEl.about}</p>}
                            </div>
                        </Link>
                    )
                    }
                )}
            </div>
        </section>
    );
};

export default MoreProjects;
